select

from exam_question
where examsId = #{examsId,jdbcType=INTEGER} and examId = #{examId,jdbcType=INTEGER} and questionBlockid = #{questionblockId,jdbcType=INTEGER}

select

from exam_question
where
examsId = #{examsId,jdbcType=INTEGER}
